import React, { useState } from "react"
import ReactDOM from "react-dom/client"
import Offers from "./Offers"

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      items: [
        { id: 0, text: "first property" },
        { id: 1, text: "second property" },
      ],
    }
    this.adder = this.adder.bind(this)
  }

  render() {
    return (
      <div>
        <h1>Easy shop</h1>
        <Offers user={this.state.items} adder={this.adder} />
      </div>
    )
  }

  adder() {
    this.setState({
      items: [
        ...this.state.items,
        { id: this.state.items.length, text: "second property" },
      ],
    })
  }
}

const root = ReactDOM.createRoot(document.getElementById("root"))
root.render(<App />)
